mapper.select:
select
id, openid, productid, productname, price, number, totle, ispay, invoice,
receive, state, address, addrid, imgs, date
from orders


    and id=#{id}


    and ispay=#{ispay}


    and receive=#{receive}


    and state=#{state}


    and openid=#{openid}


    and productid=#{productid}
